query:
mutation Checkout($items: [CheckoutItemInput!]!, $paymentMethod: String!, $cardData: CardDataInput, $freight: Float!) {
  checkout(items: $items, paymentMethod: $paymentMethod, cardData: $cardData, freight: $freight) {
    items {
      productId
      quantity
    }
    paymentMethod
    valorFinal
  }
}
